import ExpensesFilter from "./ExpensesFilter";
import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";

function Expenses(props) {
  const [year, setYear] = useState("2023");
  const expenses = props.items;
  const filteredExpenses = expenses.map((expense) => (
    <ExpenseItem
      key={expense.id}
      title={expense.title}
      amount={expense.amount}
      date={expense.date}
    />
  )).filter((selDate) => (selDate === year));

  function selectDateHandler(selectedDate) {
    setYear(selectedDate);

  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter current={year} onSelectedDate={selectDateHandler} />
        {expenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        )).filter((newArr) => (newArr = filteredExpenses))}
      </Card>
    </div>
  );
}

export default Expenses;
